// todolist 페이지
import { useEffect,useState } from "react";

const Todolist = () => {
    const [newTodo, setNewTodo] =useState('');
    const [todos, setTodos]= useState([]);
    const [editingTodoId, setEditingTodiId] = useState('');
    const [editingTodoValue, setEditingTodoValue] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [jwtToken, setJwtToken]=useState('');

    useEffect(() => {
        if (localStorage.getItem('jwt') === null) {
            window.location.replace('/signin')
        }else{
            setJwtToken(localStorage.getItem('jwt'));
        }
        // getTodos
        fetch('https://www.pre-onboarding-selection-task.shop/todos', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' +jwtToken,
            }
        })
        .then((response) => response.json())
        .then((data) =>{
            for (let i =0; i<data.length; i++){
                const getTodoItem ={
                    id: data[i].id,
                    todo: data[i].todo,
                    isCompleted: false,
                    userId: data[i].userId,
                };
                setTodos((todos) =>[...todos, getTodoItem]);
            }
        })
        .catch((error) =>{
            setErrorMessage('todo 리스트 불러오기에 실패했습니다.');
        })

      },[jwtToken]);

    const handleNewTodoChange =(e) =>{
        setNewTodo(e.target.value);
    };

    const handleAddTodo =() =>{
        if(newTodo.trim() ===''){
            return; // 빈 todo list 리턴 방지
        }

        localStorage.setItem('todos', JSON.stringify(todos));

        // createTodo
        fetch('https://www.pre-onboarding-selection-task.shop/todos', {
            method: 'POST',
            headers: {
                'Authorization': 'Bearer ' +jwtToken,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({"todo": newTodo}),
        })
        .then((response) => response.json())
        .then((data) =>{
            console.log(data.todo);
            const newTodoItem ={
                id: data.id,
                todo: data.todo,
                isCompleted: false,
                userId: data.userId,
            };
            setTodos((todos) =>[...todos, newTodoItem]);
        })
        .catch((error) =>{
            setErrorMessage('todo 리스트 추가에 실패했습니다.');
        })

        setNewTodo('');
    };

    const handleDelteTodo =(id) =>{
        // deleteTodo
        fetch('https://www.pre-onboarding-selection-task.shop/todos/'+id, {
            method: 'DELETE',
            headers: {
                'Authorization': 'Bearer ' +jwtToken,
            },
        })
        .catch((error) =>{
            setErrorMessage('todo 리스트 삭제에 실패했습니다.');
        })
        setTodos((todos) => todos.filter((todo) => todo.id !==id));
        localStorage.setItem('todos', JSON.stringify(todos));
    }

    // updateTodo
    const handleModifyTodo =(id, value) =>{
        setEditingTodiId(id)
        // setEditingTodoValue(value);
    };

    return (
      <div>
        <input 
            data-testid="new-todo-input"
            value= {newTodo}
            onChange= {handleNewTodoChange} />
        <button 
            data-testid="new-todo-add-button"
            onClick={handleAddTodo}> 추가</button>

        <ul>
            {todos.map((todo, id) =>{return(
                <li key= {id}>
                    {editingTodoId === todo.id ? (
                        <>
                            <input data-testid="modify-input" />
                            <button data-testid="submit-button">제출</button>
                            <button data-testid="cancel-button">취소</button>
                        </>
                    ):(
                        <>
                        <label>
                            <input type="checkbox" />
                            <span>{todo.todo}</span>
                        </label>
                        <button data-testid="modify-button"
                            onClick={() =>handleModifyTodo(todo.id, todo.todo)}>수정</button>
                        <button data-testid="delete-button" 
                            onClick={() => handleDelteTodo(todo.id)}>삭제</button>
                        </>
                    )}
                </li>
            )})}
        </ul>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </div>
    );
  };

  export default Todolist;
