// todolist 페이지
import { useEffect,useState } from "react";

const Todolist = () => {
    const [newTodo, setNewTodo] =useState('');
    const [todos, setTodos]= useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [jwtToken, setJwtToken]=useState('');

    useEffect(() => {
        if (localStorage.getItem('jwt') === null) {
            window.location.replace('/signin')
        }else{
            setJwtToken(localStorage.getItem('jwt'));
        }
        const storedTodos = JSON.parse(localStorage.getItem('todos'));
        if (storedTodos) {
            setTodos(storedTodos);
        }
      },[]);

    const handleNewTodoChange =(e) =>{
        setNewTodo(e.target.value);
    };

    const handleAddTodo =() =>{
        if(newTodo.trim() ===''){
            return; // 빈 todo list 리턴 방지
        }

        const newTodoItem ={
            id: Date.now(),
            text: newTodo,
            isCompleted: false,
        };

        setTodos((todos) =>[...todos, newTodoItem]);
        localStorage.setItem('todos', JSON.stringify(todos));

        fetch('https://www.pre-onboarding-selection-task.shop/todos', {
            method: 'POST',
            headers: {
                'Authorization': jwtToken,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({todo: newTodo}),
        })
        .then((response) => response.json())
        .then((data) =>{
            console.log(data);
        })
        .catch((error) =>{
            setErrorMessage('todo 리스트 추가에 실패했습니다.');
        })
        setNewTodo('');
    };

    const handleDelteTodo =(id) =>{
        setTodos((todos) => todos.filter((todo) => todo.id !==id));
        localStorage.setItem('todos', JSON.stringify(todos));
    }

    return (
      <div>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        <input 
            data-testid="new-todo-input"
            value= {newTodo}
            onChange= {handleNewTodoChange} />
        <button 
            data-testid="new-todo-add-button"
            onClick={handleAddTodo}> 추가</button>

        <ul>
            {todos.map((todo, index) =>{return(
                <li key= {index}>
                    <label>
                        <input type="checkbox" />
                        <span>{todo.text}</span>
                    </label>
                    <button data-testid="modify-button">수정</button>
                    <button data-testid="delete-button" 
                        onClick={() => handleDelteTodo(todo.id)}>삭제</button>
                </li>
            )})}
        </ul>
      </div>
    );
  };

  export default Todolist;
